    load: function() {
      var middlewareStack = middleware.load();
      if (useDefaults) {
        this.add('events');
        this.add('rpc');
      }
      var output = {};
      for (var id in responders) {
        var responder = responders[id];
        output[id] = {
          name: responder.name,
          interfaces: responder.interfaces(middlewareStack)
        };
      }
      return output;
    }
  };
};

