/**
 * @ngdoc service
 * @name utils.misc#randomString
 * @methodOf utils.misc:misc
 * @function
 *
 * @description
 * Adapted from http://www.broofa.com/Tools/Math.uuid.js
 *
 * @param {String}  len   Length of the expected string
 * @return {String}       uuid string
 */
exports.randomString = function (len) {
  var chars = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'.split(''),
      uuid  = [],
      radix = chars.length,
      i;

  for (i = 0; i < len; i++) {
    uuid[i] = chars[0 | Math.random()*radix];
  }
  return uuid.join('');
};

/**
 * @ngdoc service
 * @name utils.misc#parseWsMessage
 * @methodOf utils.misc:misc
 * @function
 *
 * @description
 * Parse incoming websocket messages into message type and contents
 *
 * @param  {String} msg Message to parse
 * @return {Array}      Array, where [0] is message type, [1] is message's body
 */
exports.parseWsMessage = function (msg) {
  var i;
  if ((i = msg.indexOf('|')) > 0) {
    return [msg.substr(0, i), msg.substr(i+1)];
  } else {
    throw new Error('Invalid message');
  }
};

/**
 * @ngdoc service
 * @name utils.misc#extend
 * @methodOf utils.misc:misc
 * @function
 *
 * @description
 * Taken from underscore.js
 *
 * @param  {Object}     obj         Object to extend
 * @param  {<Objects>}  <objects>  to extend with
 * @return {Object}                 Extended object
 */
exports.extend = function (obj) {
  var source,
      prop,
      i;

  for (i = 1; i < arguments.length; i++) {
    source = arguments[i];
    for (prop in source) {
      if (source.hasOwnProperty(prop)) {
        obj[prop] = source[prop];
      }
    }
  }
  return obj;
};

/**
 * @ngdoc service
 * @name utils.misc#defaults
 * @methodOf utils.misc:misc
 * @function
 *
 * @description
 * Useful for declaring default parameter
 *
 * @param  {Object} args      Arguments
 * @param  {Object} defaults  Default arguments
 * @return {Object}           Extended object
 */
exports.defaults = function (args, defaults) {
  return exports.extend({}, defaults, args);
};

// Websocket Event Dispatcher
// --------------------------
// Delivers events to individual (or groups of) websocket IDs 
'use strict';

var subscriptions = require('./subscriptions');

module.exports = function(eventTransport, wsTransport, emitter) {
  return eventTransport.listen(function(obj) {
    var send = wsTransport.event();

    // Emit message to the event responder (always Responder ID 0)    
    return emitter.emit('0', obj, {}, andThen());

    function andThen() {
      switch (obj.t) {
        case 'all':
          return function(msg) {
            return send.all(msg);
          };
        case 'socketId':
          return function(msg) {
            return send.socketId(obj.socketId, msg);
          };
        case 'channel':
          return function(msg) {
            return sendToMultiple(send, msg, obj.channels, 'channel');
          };
        case 'user':
          return function(msg) {
            return sendToMultiple(send, msg, obj.users, 'user');
          };
      }
    }
  });
};


// Private

// Attempt to send the event to the socket. If socket no longer exists, remove it from set
function sendToMultiple(send, msg, destinations, type) {
  destinations = destinations instanceof Array && destinations || [destinations];
  destinations.forEach(function(destination) {
    var set, socketIds;
    set = subscriptions[type];
    if ((socketIds = set.members(destination))) {
      return socketIds.slice(0).forEach(function(socketId) {
        if (!send.socketId(socketId, msg, destination)) {
          return set.removeFromAll(socketId);
        }
      });
    }
  });
  return true;
}

// Module loading code from Browserify: https://github.com/substack/node-browserify

window.require = function (file, cwd) {
    var resolved = require.resolve(file, cwd || '/');
    var mod = require.modules[resolved] || require.initModule(resolved);
    if (!mod) throw new Error(
        'Failed to resolve module ' + file + ', tried ' + resolved
    );
    return mod._cached ? mod._cached : mod();
};

require.paths = [];
require.modules = {};

require._core = {
    'events': true, // FUTURE_WORK, support events?
    'path': true,
    'vm': true // FUTURE_WORK, support vm ?
};

require.resolve = function (x, cwd) {
  if (!cwd) { cwd = '/'; }

  if (require._core[x]) {return x;}
  var path = require.modules.path();

  // strip extension
  var ext = path.extname(x);
  if (ext) {
    x = x.substring(0, x.length - ext.length);
  }

