'use strict';

var fs = require('fs'),
    path = require('path');

module.exports = function(ss, config, clientOptions) {
  config = config || {};
  if (!config.basedir) {
    config.basedir = path.join(ss.root, clientOptions.dirs.client);
  }

  var jade = ss.require('jade');

  return {

    name: 'Jade',
    extensions: ['jade'],
    assetType: 'html',
    contentType: 'text/html',

    compile: function(path, options, cb) {

      var locals = {};

      // Merge any locals passed to config.locals
      if (options.locals && typeof(options.locals) === 'object') {
        for (var attrname in options.locals) { locals[attrname] = options.locals[attrname]; }
      }

      // If passing optional headers for main view HTML
      if (options && options.headers) {
        locals.SocketStream = options.headers;
      }

      var input = fs.readFileSync(path, 'utf8');
      var parser = jade.compile(input, Object.create(config, {
        filename: {value:path}
      }) );
      var output = parser(locals);

      cb(output);
    }
  };
};

'use strict';

var fs = require('fs'),
    pathlib = require('path'),
    _prependedLess;

exports.prependLess = function(prependedLess) {
    if(typeof prependedLess === 'string') {
        _prependedLess = prependedLess;
    }
};

function errorBodyStyling(root,path, err) {
    path = path.replace(root,'');
    var filename = err.filename.replace(root,'');
    var r = [
        'body:before {',
        'display:block;',
        'position:absolute;',
        'top:15px; left:20px; right:20px;',
        'z-index: 10000;',
        'content:"Error compiling LESS file: '+path+' at '+(filename||'?')+' '+(err.line||'?')+':'+(err.index||'?')+'   '+err.message+'";',
        'border: 1px solid #888;',
        'border-radius: 5px;',
        'background-color: rgba(255,255,255,.5);',
        'background-color: white;',
        'padding: 10px 15px;',
        '}'
    ];
    return r.join('\n');
}

exports.decidePaths = function decidePaths(path, options) {

    var p = [ pathlib.dirname(path) ];
    //TODO by default we should add the ss.root to paths at end
    if (options.paths) {
        p = p.concat(options.paths);
    }
    return p;
};

// root and config are passed as arguments, but are not used
//
module.exports = function (ss,config) {

    var less = ss.require('less');

    return {

        name: 'Less',
        extensions: ['less'],
        assetType: 'css',
        contentType: 'text/css',

        compile: function(path, options, cb) {

            var input = fs.readFileSync(path, 'utf8');

            if(_prependedLess) {
                input = _prependedLess + '\n' + input;
            }
            var opts = {};
            for(var n in config) { opts[n] = config[n]; }
            opts.filename = path;
            opts.paths = exports.decidePaths(path, config);
            opts.compress = options.compress;

            less.logger.addListener({
                //TODO passing along log output
                debug: function(/*msg*/) {

                },
                info: function(/*msg*/) {

                },
                warn: function(/*msg*/) {

                },
                error: function(/*msg*/) {

                }
            });

            //TODO it seems less still blows up on some path errors, is there a way to catch them?

            less.render(input, opts, function(err, out) {
                if (err) {
                    console.log('! - Unable to compile Less file %s into CSS', path);
                    console.log(err);
                    return cb(errorBodyStyling(ss, path, err));
                }
                //TODO out.map
                cb(out.css);
            });
        }
    };
};

'use strict';

var path = require('path'),
    findup = require('findup-sync');

module.exports = function(ss, config, clientOptions) {

  var sass = ss.require('node-sass'),
      modulesPath = [ findup('node_modules') ]; //TODO get real npm and bower package locations

  console.log(  );

  return {

    name: 'Sass',

    extensions: ['sass', 'scss'],

    assetType: 'css',

    contentType: 'text/css',

    compile: function(srcPath, options, success, error) {

      var opts = {
        file: srcPath,
        includePaths: modulesPath.concat([ path.join(ss.root, clientOptions.dirs.client) ])
      };
      for(var n in config) {
        if (n !== 'file') {
          opts[n] = config[n] || opts[n];
        }
      }
      opts.locals = opts.locals || options.locals;
      opts.outputStyle = options.compress? "compressed":"nested";

      sass.render(opts, function(err, result) {
        if (err) {
          console.log(err)
          return error(err);
        }
        success(result.css.toString());
        //TODO result.stats print
        //TODO result.map.toString
      });
    }
  };
};

// Produces inline templates for Angular.js
// Use them in conjunction with `ng-view`. Note the `.html` at the end of each template name which is automatically appended

'use strict';

exports.init = function() {
  return {
    name: 'Angular.js',
    process: function(template, path, id) {
      return '<script type="text/ng-template" id="' + id + '.html">' + template + '</script>';
    }
  };
};

// Produces templates for Ember.js
// Note Ember compiles these Handlebars templates one time only when you call Em.Application.create();
'use strict';

exports.init = function() {
  return {
    name: 'Ember.js',
    process: function(template, path, id) {
      return '<script type="text/x-handlebars" data-template-name="' + id + '">' + template + '</script>';
    }
  };
};

// Publish Event - Internal EventEmitter Transport
'use strict';

var EventEmitter2, emitter;

EventEmitter2 = require('eventemitter2').EventEmitter2;

emitter = new EventEmitter2();

module.exports = function() {
  return {
    listen: function(cb) {
      return emitter.on('event', cb);
    },
    send: function(obj) {
      return emitter.emit('event', obj);
    }
  };
};

// Publish Event - Redis Transport
'use strict';

var redis = require('redis'); //TODO ss.require('redis')

module.exports = function(config) {
  var conn, host, options, port;
  if (!config) {
    config = {};
  }

  // Set options or use the defaults  
  port = config.port || 6379;
  host = config.host || '127.0.0.1';
  options = config.options || {};

  // Redis requires a separate connection for pub/sub  
  conn = {};
  ['pub', 'sub'].forEach(function(name) {
    conn[name] = redis.createClient(port, host, options);
    if (config.pass) {
      conn[name].auth(config.pass);
    }
    if (config.db) {
      return conn[name].select(config.db);
    }
  });
  return {
    listen: function(cb) {
      conn.sub.subscribe('ss:event');
      return conn.sub.on('message', function(channel, msg) {
        return cb(JSON.parse(msg));
      });
    },
    send: function(obj) {
      var msg;
      msg = JSON.stringify(obj);
      return conn.pub.publish('ss:event', msg);
    }
  };
};

'use strict';
// Server-side Code

// Define actions which can be called from the client using ss.rpc('demo.ACTIONNAME', param1, param2...)
exports.actions = function(req, res, ss) {

  // Example of pre-loading sessions into req.session using internal middleware
  req.use('session');

  // Uncomment line below to use the middleware defined in server/middleware/example
  //req.use('example.authenticated')

  return {

    sendMessage: function(message) {
      if (message && message.length > 0) {         // Check for blank messages
        ss.publish.all('newMessage', message);     // Broadcast the message to everyone
        return res(true);                          // Confirm it was sent to the originating client
      } else {
        return res(false);
      }
    }

  };

};
